INSERT INTO `email_templates` (`id`, `name`, `subject`, `description`, `created_at`, `updated_at`) VALUES
(1, 'partner_registration', 'Registration', '<p>&lt;div style=&quot;text-align: center;&quot;&gt;<br />\n&nbsp; &nbsp; &lt;img src=&quot;{{$logo}}&quot; alt=&quot;Company Logo&quot; style=&quot;width: 100px; height: 100px;&quot;&gt;<br />\n&lt;/div&gt;<br />\nDear {{$username}},</p>\n\n<p>We&#39;re excited to have you join our community!</p>\n\n<p>Thank you for signing up. Please take a moment to log in to your account. Once logged in, we encourage you to complete your profile as soon as possible. Your time is important to us!</p>\n\n<p>Log In to Your Account: https://astrowaypro.diploy.in</p>\n\n<p>★★★★★</p>\n\n<p>If you have any questions or need assistance, don&#39;t hesitate to reach out to our support team. We&#39;re here to help you with anything you need.</p>\n\n<p>We&#39;re looking forward to seeing you on Astrowaypro!</p>\n\n<p>Thanks and Regards,<br />\nMyAstro Team</p>\n\n<p>&copy; 2025 Astrowaypro. All rights reserved.</p>', '2025-03-21 04:52:17', '2025-03-21 10:08:04'),
(2, 'verify', 'Verification', '<p>&lt;div style=&quot;text-align: center;&quot;&gt;<br />\r\n&nbsp; &nbsp; &lt;img src=&quot;{{$logo}}&quot; alt=&quot;Company Logo&quot; style=&quot;width: 100px; height: 100px;&quot;&gt;<br />\r\n&lt;/div&gt;<br />\r\n<strong>Dear {{$username}},</strong></p>\r\n\r\n<p>Congratulations!</p>\r\n\r\n<p>Your application to join us as an Astrologer has been shortlisted for the next round.</p>\r\n\r\n<p>In order to proceed further, please log in with your registered phone number and complete the profile. Please note that this is a mandatory step to join our interview; otherwise, your application will stay on hold.</p>\r\n\r\n<p>Our Office will contact you and process your application after the initial interview. Once you are approved, you will get a confirmation mail to start the activity with our customers on our site.</p>\r\n\r\n<p>★★★★★</p>\r\n\r\n<p>We wish you all the best!</p>\r\n\r\n<p>Thanks and Regards,<br />\r\nAstrowaypro Onboarding Team</p>\r\n\r\n<p><strong>&copy; 2025 Astrowaypro. All rights reserved.</strong></p>', '2025-03-21 05:10:40', '2025-03-21 10:07:52'),
(3, 'unverify', 'Unverify Account', '<p>&lt;div style=&quot;text-align: center;&quot;&gt;<br />\r\n&nbsp; &nbsp; &lt;img src=&quot;{{$logo}}&quot; alt=&quot;Company Logo&quot; style=&quot;width: 100px; height: 100px;&quot;&gt;<br />\r\n&lt;/div&gt;<br />\r\n<strong>Dear {{$username}},</strong></p>\r\n\r\n<p>We are sorry to inform you that your application to join us as an Astrologer has been rejected.</p>\r\n\r\n<p>In order to proceed further, please log in with your registered phone number and complete the profile if any further information is required.</p>\r\n\r\n<p>We wish you all the best for your future endeavors!</p>\r\n\r\n<p>Thanks and Regards,<br />\r\nAstrowayproOnboarding Team</p>\r\n\r\n<p><strong>&copy; 2025 Astrowaypro. All rights reserved.</strong></p>', '2025-03-21 05:10:40', '2025-03-21 10:08:15'),
(4, 'partner_register_otp', 'Partner Register OTP', '<div style=\"text-align: center;\">\r\n  <img src=\"{{$logo}}\" alt=\"Company Logo\" style=\"width: 100px; height: 100px;\">\r\n</div>\r\n<p>Dear user,</p>\r\n\r\n<p>Your one-time password (OTP) for {{$company_name}} is:</p>\r\n\r\n<div style=\"text-align: center; font-size: 24px; font-weight: bold; margin: 20px 0;\">\r\n  {{$otp}}\r\n</div>\r\n\r\n<p>Please use this code to complete your login or account verification. This code is valid for a limited time and should not be shared with anyone.</p>\r\n\r\n<p>If you did not request this OTP, please ignore this email.</p>\r\n\r\n<p>Thanks and Regards,<br>\r\nThe {{$company_name}} Team</p>\r\n\r\n<p>© 2025 {{$company_name}}. All rights reserved.</p>', '2025-09-03 04:52:17', '2025-09-03 04:52:17');
